parameters.jsCode:
// Make Short Code
const phone = $('Normalize + prepare fields').item.json.phone || '';
const tail = phone.slice(-4);
const rand = Math.floor(100 + Math.random() * 900); // 3-digit
const sc = `TF${tail}${rand}`;
return [{ json: { short_code: sc } }];

